import networkimport _thread as thimport timeimport machine, ubinascii, gc, json CLIENT_ID = ubinascii.hexlify(machine.unique_id())def conncb(task):    print("[{}] Connected".format(task))    mqtts.subscribe("/line/bot/gpio")def disconncb(task):    print("[{}] Disconnected".format(task))def subscb(task):    print("[{}] Subscribed".format(task))def pubcb(pub):    print("[{}] Published: {}".format(pub[0], pub[1]))def datacb(msg):    print("[{}] Data arrived from topic: {}, Message: ".format(msg[0], msg[1]), msg[2])    mqtts = network.mqtt("myx", "103.13.228.61", secure=True, autoreconnect=True,   clientid=CLIENT_ID, keepalive=30, cleansession=True,connected_cb=conncb,   disconnected_cb=disconncb, subscribed_cb=subscb, published_cb=pubcb, data_cb=datacb)time.sleep(2)mqtts.start()def loop():  while True:    time.sleep(5)    print('Trick: ', mqtts.status(), gc.mem_free())    gc.collect()    th.start_new_thread('loop',loop, ()) 