from machine import I2C, Pinimport ssd1306rst = Pin(16, Pin.OUT)rst.value(1)scl = Pin(15, Pin.OUT, Pin.PULL_UP)
sda = Pin(4, Pin.OUT, Pin.PULL_UP)i2c = I2C(scl=scl, sda=sda, freq=450000)oled = ssd1306.SSD1306_I2C(128, 64, i2c,  addr=0x3c)oled.fill(0)oled.text('ESP32', 45, 5)oled.text('MicroPython', 20, 20)oled.text('T: {0:.2f}'.format(27.50), 3, 35) oled.text('H: {0:.2f}'.format(68.20), 3, 50)oled.show() 